$(document).ready(function(){$("#mainForm").submit(function(b){b.preventDefault();var c=$(this).serializeArray();var a={};$.each(c,function(){a[this.name]=this.value||""});a=JSON.stringify(a);$.ajax({type:"POST",url:"/create",dataType:"json",contentType:"application/json",data:a}).done(function(e){var d=e.url;$("#resultBox").show();$("#link").text(d)}).fail(function(d){console.log(d)})});$("#link").click(function(){var a=$("<input>");$("body").append(a);a.val($("#link").text()).select();document.execCommand("copy");a.remove();console.log("copied to clipboard! "+$(this).text())})});